            </b-nav-item>
            <b-nav-item>
              <!-- <b-button v-b-modal.modal-login variant="link" style="color:white">Sign In</b-button> -->
              <b-button v-b-modal.modal-register variant="primary">Sign Up</b-button>
            </b-nav-item>
          </b-navbar-nav>

          <b-navbar-nav v-else class="ml-auto">
            <!-- <b-nav-item v-if="!isAunteticated">
              <b-button v-b-modal.modal-login variant="link" style="color:white">Sign In</b-button>
              <b-button v-b-modal.modal-register variant="primary">Sign Up</b-button>
            </b-nav-item> -->
            <b-nav-item>
              <div style="display:flex;">
                <b-avatar variant="info" :src="profilPic" />
                <b-nav-item-dropdown right>
                  <!-- Using 'button-content' slot -->
                  <template v-slot:button-content>
                    <em style="color:white">{{ name }}</em>
                  </template>
                  <b-dropdown-item @click="()=>routeme('profile')">Profile</b-dropdown-item>
                  <b-dropdown-item @click="signingOut">Sign Out</b-dropdown-item>
                </b-nav-item-dropdown>
              </div>
