    <b-modal id="modal-login" ref="modal-login" title="Login Page" hide-footer>
      <div>
        <b-form v-if="show" @submit="onSubmit" @reset="onReset">
          <b-form-group
            id="input-group-1"
            label="Email address:"
            label-for="input-1"
            description="We'll never share your email with anyone else."
          >
            <b-form-input
              id="input-1"
              v-model="form.email"
              type="email"
              required
              placeholder="Enter email"
            />
          </b-form-group>

          <b-form-group id="input-group-2" label="Password" label-for="input-2">
            <b-form-input
              id="input-2"
              v-model="form.password"
              type="password"
              required
              placeholder="Enter password"
            />
          </b-form-group>
          <b-button type="submit" variant="primary">Submit</b-button>
          <b-button type="reset" variant="danger">Reset</b-button>
        </b-form>
      </div>
    </b-modal>
    <b-modal id="modal-register" ref="modal-register" title="Sign UP" hide-footer>
      <div>
        <b-form v-if="show" @submit="onSubmit" @reset="onReset">
          <b-form-group id="example-input-group-1" label="Name" label-for="example-input-1">
            <b-form-input
              id="example-input-1"
              v-model="register.name"
              v-validate="{ required: true, min: 3 }"
              name="example-input-1"
              :state="validateState('example-input-1')"
              aria-describedby="input-1-live-feedback"
              data-vv-as="Name"
              placeholder="masukan nama lengkap anda"
            />

            <b-form-invalid-feedback id="input-1-live-feedback">{{ veeErrors.first('example-input-1') }}</b-form-invalid-feedback>
          </b-form-group>
          <b-form-group
            id="input-group-1"
            label="Email address:"
            label-for="input-1"
            description="kami akan menjaga kerahasiaan emailmu."
          >
            <b-form-input
              id="email"
              v-model="register.email"
              v-validate="'required|email'"
              type="email"
              name="email"
              :state="validateState('email')"
              aria-describedby="input-4-live-feedback"
              data-vv-as="Email"
              placeholder="Enter email"
            />
            <b-form-invalid-feedback id="input-4-live-feedback">{{ veeErrors.first('email') }}</b-form-invalid-feedback>
          </b-form-group>

          <b-form-group id="input-group-2" label="Password" label-for="input-2">
            <b-form-input
              id="password"
              ref="password"
              v-model="register.pass"
              v-validate="{ required: true, min: 6}"
              type="password"
              name="password"
              :state="validateState('password')"
              aria-describedby="input-2-live-feedback"
              data-vv-as="password"
              placeholder="Enter password"
            />
            <b-form-invalid-feedback id="input-2-live-feedback">{{ veeErrors.first('password') }}</b-form-invalid-feedback>

          </b-form-group>

          <b-form-group id="input-group-2" label="Verify Password" label-for="input-2">
            <b-form-input
              id="verif-password"
              v-model="register.verif"
              v-validate="{ required: true,confirmed:'password'}"
              type="password"
              name="verif-password"
              :state="validateState('verif-password')"
              aria-describedby="input-3-live-feedback"
              data-vv-as="verif-password"
              placeholder="Verify password"
            />
            <b-form-invalid-feedback id="input-3-live-feedback">{{ veeErrors.first('verif-password') }}</b-form-invalid-feedback>
          </b-form-group>
          <b-button type="submit" variant="primary" @click="onRegist">Submit</b-button>
          <b-button type="reset" variant="danger">Reset</b-button>
        </b-form>
      </div>
    </b-modal>
  </div>
</template>;

<script>
import router from '../../../../routes';
import {userdetailFetch} from '../../../../utils/apiUtils';
export default {
  name: 'Topbar',
  data() {
    return {
      logo: '../images/logo.png',
      form: {
        email: '',
        password: '',

      },
      register: {
        name: '',
        email: '',
        pass: '',
        verif: '',
      },
      show: true,
      // isAunteticated: false,
    };
  },
  created() {
    if (localStorage.getItem('@token')) {
      this.$store.dispatch('auth/getVerifyToken', localStorage.getItem('@id'));
      this.getUserDetail();
    }
  },
  computed: {
    isAunteticated: function() {
      return this.$store.getters.isAunteticated;
    },
    name: function() {
      return this.$store.getters.name;
    },
    profilPic: function() {
      const {ProfilePic} = this.$store.getters;
      if (ProfilePic) {
        return `../storage/profilPic/${ProfilePic}`;
      }
      return '../storage/profilPic/origin.jpg';
    },
  },
  methods: {
    async getUserDetail() {
      console.log('getUserDetail');
      try {
        const id = localStorage.getItem('@id');
        const sendapi = await userdetailFetch(id);
        if (sendapi.data.general_detail != null) {
          this.$store.dispatch('info/setDetailState', sendapi.data);
        }
        // console.log('DOB VUEX', this.$store.getters.DOB);
      } catch (error) {
        console.log(error, 'errPersonal');
        this.showAlert('Uppss,Internal Server Error', 'failed');
      }
    },
    validateState(ref) {
      if (
        this.veeFields[ref] &&
        (this.veeFields[ref].dirty || this.veeFields[ref].validated)
      ) {
        return !this.veeErrors.has(ref);
      }
      return null;
    },
    async onSubmit(evt) {
      evt.preventDefault();
      const jsonData = JSON.parse(JSON.stringify(this.form));
      const SendApi= await this.$store.dispatch('auth/setUserLogin', jsonData);
      console.log('SendApi', SendApi);
      if (SendApi==='true') {
        this.$refs['modal-login'].hide();
        this.$swal({icon: 'success',
          title: 'Login Successfull'});
        this.getUserDetail();
      } else {
        this.$refs['modal-login'].hide();
        alert('login Failed');
      }
    },
    async onRegist(evt) {
      evt.preventDefault();
      const jsonData = JSON.parse(JSON.stringify(this.register));
      const SendApi= await this.$store.dispatch(
          'auth/setUserRegister', jsonData);
      if (SendApi==='true') {
        this.$refs['modal-login'].hide();
        this.$swal({icon: 'success',
          title: 'Register Successfull'});
      } else {
        this.$refs['modal-login'].hide();
        alert('Register Failed');
      }
    },
    onReset(evt) {
      evt.preventDefault();
      // Reset our form values
      this.form.email = '';
      this.form.password = '';
    },
    async signingOut(evt) {
      const response= await this.$store.dispatch('auth/userLogout');
      console.log('response', response);
      if (response.success==='false') {
        this.$swal({icon: 'error',
          title: 'Logout failed'});
      } else {
        this.$swal({icon: 'success',
          title: 'Logout Success'});
      }
    },
    routeme(name) {
      router.push({name});
    },
  },
};
</script>

